CREATE DATABASE IF NOT EXISTS aves_mallorca;
USE aves_mallorca;

CREATE TABLE Parques_Jardines (
id_parque INT AUTO_INCREMENT PRIMARY KEY,
nombre VARCHAR(150) NOT NULL,
ubicacion VARCHAR(255),
latitud DECIMAL(10,7) NULL,
longitud DECIMAL(10,7) NULL,
url VARCHAR(255),
imagen VARCHAR(255)
);


CREATE TABLE Equipo (
id_miembro INT AUTO_INCREMENT PRIMARY KEY,
nombre VARCHAR(150) NOT NULL,
rol VARCHAR(150) NOT NULL,
descripcion TEXT,
especializacion VARCHAR(255),
fotografia VARCHAR(255),
orden INT DEFAULT 0
);

CREATE TABLE Aves (
id_ave INT AUTO_INCREMENT PRIMARY KEY,
nombre_cientifico VARCHAR(150) NOT NULL UNIQUE,
nombre_comun VARCHAR(150),
familia VARCHAR(100),
tipo_ave ENUM(
'Accipitriformes', 'Anseriformes', 'Apodiformes', 'Bucerotiformes', 'Charadriiformes', 'Ciconiiformes',
'Columbiformes', 'Coraciiformes', 'Falconiformes', 'Galliformes', 'Gruiformes',
'Passeriformes', 'Pelecaniformes', 'Phoenicopteriformes', 'Piciformes',
'Psittaciformes', 'Sphenisciformes', 'Strigiformes',
'Procellariiformes', 'Podicipediformes', 'Otros'
) NOT NULL,
endemica BOOLEAN DEFAULT FALSE,
descripcion TEXT,
peso ENUM('muy ligero', 'ligero', 'medio', 'pesado', 'muy pesado'),
tamano ENUM('muy pequeño', 'pequeño', 'mediano', 'grande', 'muy grande'),
vida_media INT,
habitat_y_cria TEXT,
area_invernada TEXT,
patron_migratorio TEXT,
categoria_conservacion ENUM(
'Peligro de extinción',
'Sensibles a la alteración de su hábitat',
'Vulnerables',
'De interés especial',
'De especial protección',
'Otros',
'Invasoras'),
plan_recuperacion VARCHAR(255) ,
epoca_reproduccion ENUM('Primavera', 'Verano', 'Otoño', 'Invierno', 'Todo el año'),
curiosidades TEXT,
fotografias VARCHAR(255),
id_parque INT,
CONSTRAINT fk_parque
FOREIGN KEY (id_parque)
REFERENCES Parques_Jardines(id_parque)
ON UPDATE CASCADE
ON DELETE SET NULL
);


INSERT INTO `Parques_Jardines` VALUES (1,'Parc Natural de s’Albufera de Mallorca','Norte de Mallorca, municipios de Muro y Sa Pobla',39.7769120,3.1319590,'https://www.illesbalears.travel/en/mallorca/nature-park-s-albufera-de-mallorca','../imgLugares/ParcNaturaldes’AlbuferadeMallorca.jpg'),(2,'Reserva Natural de s’Albufereta','Bahía de Pollença / Alcúdia, norte de Mallorca',39.7968370,3.1089470,'https://www.birdinginspain.com/sites-anditineraries/mallorca/albufereta','../imgLugares/ReservaNaturaldesAlbufereta.jpg'),(3,'Parc Natural de la Península de Llevant','Noreste de Mallorca, municipio de Artà',39.7040000,3.3755000,'https://www.seemallorca.com/sights/reserves/llevant-peninsula-nature-reserve-mallorca','../imgLugares/ParcNaturaldelaPeninsuladeLlevant.jpg'),(4,'Parc Natural de Mondragó','Sureste de Mallorca, municipio de Santanyí',39.3558000,3.1777900,'https://es.wikipedia.org/wiki/Parque_natural_de_Mondrag%C3%B3n','../imgLugares/ParcNaturaldeMondrago.jpg'),(5,'Parc Natural de sa Dragonera','Isla Dragonera, suroeste de Mallorca',39.5708300,2.4213900,'https://es.wikipedia.org/wiki/Parque_natural_de_Sa_Dragonera','../imgLugares/ParcNaturaldesaDragonera.jpg'),(6,'Parc Natural Maritimoterrestre es Trenc-Salobrar de Campos','Sur de Mallorca, Campos',39.3444000,2.9925000,'https://es.wikipedia.org/wiki/Es_Trenc-Salobrar_de_Campos','../imgLugares/ParcNaturalMaritimoterrestreesTrenc–SalobrardeCampos.jpg'),(7,'Paratge Natural de la Serra de Tramuntana','Zona montañosa noroeste de Mallorca',39.8077000,2.7936100,'https://es.wikipedia.org/wiki/Sierra_de_Tramontana','../imgLugares/ParatgeNaturaldelaSerradeTramuntana.jpg'),(8,'Monument Natural de ses Fonts Ufanes','Campanet, Mallorca norte',39.7795000,2.9507000,'https://es.wikipedia.org/wiki/Fonts_Ufanes','../imgLugares/MonumentNaturaldesesFontsUfanes.jpg'),(9,'Monument Natural del Torrent de Pareis','Escorca, Serra de Tramuntana',39.8415000,2.7850000,'https://es.wikipedia.org/wiki/Torrent_de_Pareis','../imgLugares/MonumentNaturaldelTorrentdePareis.jpg'),(10,'Centro Sanitario Municipal de Son Reus','Palma de Mallorca',39.6404000,2.7275000,'https://www.palma.cat/portal/PALMA/son-reus','../imgLugares/CentroSanitarioMunicipaldeSonReus.jpg'),(11,'Centro de Recuperación de Tortugas Marinas de la Fundación Marineland-Mallorca','Portals Nous, Calvià',39.5264000,2.5691000,'https://www.marineland.es/mallorca/','../imgLugares/CentrodeRecuperaciondeTortugasMarinas.jpg'),(12,'Consorcio para la Recuperación de Fauna de las Islas Baleares (COFIB)','Santa Eugènia, Mallorca',39.6189000,2.8384000,'https://www.caib.es/','../imgLugares/COFIB.jpg'),(13,'Jardines de Alfabia','Bunyola, Mallorca',39.7160500,2.6906940,'https://es.wikipedia.org/wiki/Jardines_de_Alfabia','../imgLugares/JardinesdeAlfabia.jpg');

INSERT INTO `Aves` VALUES (1,'Puffinus mauretanicus','Pardela balear','Procellariidae','Procellariiformes',1,'Ave marina endémica de las Islas Baleares, especializada en el mar abierto y en la cría en acantilados y cuevas costeras. Su comportamiento nocturno en el nido y su alimentación basada en peces y calamares la hacen muy vulnerable a la contaminación marina y a los depredadores introducidos en las islas.','ligero','mediano',30,'Mar abierto, acantilados de cría','Atlántico oriental y Mediterráneo occidental','Migradora postcría hacia el Atlántico','Peligro de extinción','Plan de recuperación del virot petit','Primavera','Principal causa de mortalidad: depredadores introducidos','../imgAves/puffinus_mauretanicus.jpg',5),(2,'Falco eleonorae','Halcón de Eleonor','Falconidae','Falconiformes',0,'Rapaz estival mediterránea que nidifica en acantilados costeros y se alimenta principalmente de aves migratorias. Exhibe un comportamiento de caza excepcional durante la migración otoñal y ajusta su época de cría a la llegada de presas. Esta especie depende de hábitats costeros protegidos y de zonas con poca perturbación humana para mantener sus poblaciones estables.','ligero','mediano',20,'Acantilados costeros y zonas rocosas','África subsahariana','Migradora estival','De interés especial',NULL,'Verano','Cría tardía aprovechando migraciones otoñales','../imgAves/falco_eleonorae.jpg',5),(3,'Aegypius monachus','Buitre negro','Accipitridae','Accipitriformes',0,'Gran rapaz carroñera que ha sido reintroducida en la Serra de Tramuntana. Se alimenta de cadáveres de animales grandes, desempeñando un papel esencial en el ecosistema. Su lento crecimiento y baja tasa de reproducción lo hacen vulnerable a perturbaciones humanas, intoxicaciones y envenenamientos accidentales, por lo que su conservación requiere seguimiento activo y programas de reintroducción bien planificados.','pesado','grande',35,'Bosques de montaña con árboles grandes','Serra de Tramuntana','Residente','De especial protección','Programa de reintroducción en Mallorca','Todo el año','Sensibles a venenos y disturbios','../imgAves/aegypius_monachus.jpg',7),(4,'Milvus milvus','Milano real','Accipitridae','Accipitriformes',0,'Rapaz diurna recuperada en Mallorca que se encuentra en áreas agrícolas y forestales, alimentándose de pequeños mamíferos, carroña y restos de cosechas. Su presencia es visible y contribuye al equilibrio ecológico, pero necesita corredores seguros y zonas de alimentación suplementaria para mantener la población estable, evitando conflictos con actividades humanas intensivas.','ligero','mediano',25,'Áreas agrícolas y forestales','Campos abiertos','Residente','De especial protección','Programa de alimentación suplementaria','Todo el año','Una de las rapaces más visibles','../imgAves/milvus_milvus.jpg',7),(5,'Pandion haliaetus','Águila pescadora','Pandionidae','Accipitriformes',0,'Especialista en la caza de peces en ríos, lagos y embalses, utilizando técnicas de buceo precisas. Sus áreas de cría requieren estructuras naturales o artificiales seguras y protección frente a perturbaciones humanas. Es un ave de gran interés conservacionista, dado que solo unas pocas parejas se reproducen en Mallorca y su población es sensible a cambios en la calidad del agua y la disponibilidad de presas.','medio','grande',20,'Litoral y embalses','Costa mediterránea','Residente parcial','De especial protección',NULL,'Primavera','Solo unas pocas parejas reproductoras','../imgAves/pandion_haliaetus.jpg',NULL),(6,'Ichthyaetus audouinii','Gaviota de Audouin','Laridae','Charadriiformes',0,'Gaviota mediterránea que nidifica en playas tranquilas y salinas, mostrando gran fidelidad al lugar de cría. Se alimenta de peces y pequeños invertebrados, y es muy sensible a la perturbación humana. Su conservación depende de la protección de los hábitats costeros y de la gestión de colonias, que pueden verse afectadas por actividades recreativas y la degradación ambiental.','ligero','mediano',25,'Litoral y salinas','Litoral sur de Mallorca','Residente','De interés especial',NULL,'Verano','Emblemática del Mediterráneo','../imgAves/ichthyaetus_audouinii.jpg',4),(7,'Sternula albifrons','Charrancito común','Laridae','Charadriiformes',0,'Pequeña ave costera que cría en playas arenosas y salinas, muy sensible a la presencia humana y a depredadores. Su ciclo reproductivo requiere áreas protegidas y aisladas, y su dieta consiste en pequeños peces e invertebrados. La conservación de esta especie implica la protección de sus colonias y la gestión de zonas costeras para minimizar molestias durante la reproducción.','ligero','pequeño',15,'Playas arenosas y salinas','Litoral sur','Estival','De interés especial',NULL,'Verano','Nidifica en la arena','../imgAves/sternula_albifrons.jpg',4),(8,'Charadrius alexandrinus','Chorlitejo patinegro','Charadriidae','Charadriiformes',0,'Ave limícola de playas y dunas, que anida en arena expuesta y se alimenta de insectos y crustáceos. Muy sensible a la actividad humana y al pisoteo de sus nidos, depende de zonas protegidas para reproducirse con éxito. Su conservación requiere educación ambiental y control de visitantes en playas y salinas.','ligero','pequeño',12,'Playas y salinas','Playas del sur','Residente parcial','De interés especial',NULL,'Primavera','Pérdida de nidos por uso recreativo','../imgAves/charadrius_alexandrinus.jpg',4),(9,'Himantopus himantopus','Cigüeñuela común','Recurvirostridae','Charadriiformes',0,'Ave de lagunas poco profundas y aguamales, reconocible por sus patas largas y delgadas. Se alimenta de insectos, crustáceos y pequeños moluscos, y nidifica en bordes de aguas someras. Su presencia indica ecosistemas húmedos saludables y su conservación requiere la protección de humedales y la minimización de perturbaciones humanas durante la reproducción.','ligero','mediano',15,'Aiguamolls, basses poco profundas','Humedales de Mallorca','Estival','De interés especial',NULL,'Verano','Indicadora de buena calidad del agua','../imgAves/himantopus_himantopus.jpg',1),(10,'Ardea purpurea','Garza imperial','Ardeidae','Pelecaniformes',0,'Gran garza que habita cañaverales extensos, alimentándose de peces, anfibios e invertebrados acuáticos. Nidifica en colonias dispersas y es sensible a la alteración de su hábitat, por lo que requiere humedales bien conservados. Su presencia refleja la salud de los ecosistemas palustres y es una especie de interés para la conservación estival en Baleares.','medio','grande',20,'Cañizales de s’Albufera','África','Migradora estival','De interés especial',NULL,'Primavera','Cría irregular en años secos','../imgAves/ardea_purpurea.jpg',1),(11,'Ixobrychus minutus','Avetorillo común','Ardeidae','Pelecaniformes',0,'Pequeña garza críptica que habita cañaverales densos, muy difícil de observar por su camuflaje. Se alimenta de peces pequeños e invertebrados acuáticos y nidifica en vegetación emergente, protegida del disturbio humano. Su presencia indica la buena calidad de humedales y requiere conservación de zonas de vegetación densa para reproducirse de manera segura.','ligero','pequeño',12,'Carrizales y juncales','Mediterráneo','Estival','De interés especial',NULL,'Primavera','Muy difícil de observar','../imgAves/ixobrychus_minutus.jpg',1),(12,'Porphyrio porphyrio','Calamón común occidental','Rallidae','Gruiformes',0,'Ave de humedales mediterráneos, muy llamativa por su plumaje azul brillante y su pico rojo. Se alimenta de plantas acuáticas, insectos y pequeños vertebrados. Nidifica en vegetación densa de humedales y es emblemática en la conservación de estos ecosistemas, donde su presencia indica la calidad ambiental de los lagos y canales costeros.','medio','mediano',18,'Aiguamolls con vegetación emergente','Humedales costeros','Residente','De especial protección',NULL,'Todo el año','Especie emblemática de los humedales','../imgAves/porphyrio_porphyrio.jpg',1),(13,'Motacilla flava','Lavandera boyera','Motacillidae','Passeriformes',0,'Pequeña ave insectívora de prados húmedos, reconocible por su cola larga y movimientos constantes. Se alimenta de insectos y arañas, y es migradora hacia África. Durante el paso primaveral pueden observarse diversas subespecies, siendo un indicador de la salud de los ecosistemas agrícolas y prados húmedos. Su conservación depende de la preservación de áreas de alimentación y descanso durante la migración.','ligero','pequeño',8,'Campos y prados húmedos','África','Migradora','De interés especial',NULL,'Primavera','Diversas subespecies en paso primaveral','../imgAves/motacilla_flava.jpg',NULL),(14,'Coracias garrulus','Carraca europea','Coraciidae','Coraciiformes',0,'Colorida ave insectívora que habita campos abiertos y zonas agrícolas con cavidades naturales para anidar. Se alimenta principalmente de insectos grandes, mostrando hábitos de caza activos. Su población se encuentra en regresión por la pérdida de cavidades y la transformación del paisaje, por lo que la conservación de hábitats tradicionales y setos es esencial para mantener colonias reproductoras saludables.','ligero','mediano',15,'Campos agrícolas con cavidades','África','Migradora estival','Vulnerables',NULL,'Verano','En regresión por pérdida de cavidades','../imgAves/coracias_garrulus.jpg',NULL),(15,'Merops apiaster','Abejaruco europeo','Meropidae','Coraciiformes',0,'Nidifica en colonias en taludes arenosos, donde excava galerías para reproducirse. Su plumaje multicolor lo hace fácilmente reconocible y se alimenta principalmente de abejas y otros insectos voladores. Es gregario y altamente social, y requiere la protección de taludes adecuados para mantener poblaciones estables y evitar conflictos con actividades humanas en el entorno agrícola.','ligero','mediano',10,'Campos y taludes arenosos','África','Migradora estival','De interés especial',NULL,'Verano','Muy colorido y gregario','../imgAves/merops_apiaster.jpg',NULL),(16,'Upupa epops','Abubilla','Upupidae','Bucerotiformes',0,'Ave muy característica por su cresta erectil y su plumaje pardo-anaranjado con franjas negras y blancas. Habita campos, olivares y jardines, alimentándose de insectos y larvas de suelo. Su presencia indica paisajes tradicionales bien conservados y es apreciada por su canto peculiar y comportamiento curioso. Requiere setos y árboles para anidar y descansar durante todo el año.','ligero','pequeño',10,'Campos, olivares y jardines','Campos abiertos','Residente','De interés especial',NULL,'Primavera','Indicador de paisajes tradicionales','../imgAves/upupa_epops.jpg',NULL),(17,'Curruca melanocephala','Curruca cabecinegra','Sylviidae','Passeriformes',0,'Pequeña ave de matorral mediterráneo, muy activa y ruidosa, que se alimenta de insectos y bayas. Habita garrigas, máquias y setos densos, siendo común en zonas arbustivas. Su presencia indica ecosistemas saludables y estables, y la protección de los matorrales y setos donde nidifica es clave para su conservación en Baleares.','ligero','pequeño',8,'Garrigas, máquias y setos','Mediterráneo','Residente','De interés especial',NULL,'Primavera','Muy común en zonas arbustivas','../imgAves/curruca_melanocephala.jpg',NULL),(18,'Sylvia balearica','Curruca balear','Sylviidae','Passeriformes',1,'Ave endémica balear que habita matorrales litorales y garrigas ventosas. Se alimenta de insectos y pequeños frutos, y es sensible a la perturbación costera. Su conservación requiere protección de hábitats y programas de seguimiento, ya que es un indicador de la calidad de los ecosistemas insulares y un elemento valioso del patrimonio natural de Baleares.','ligero','pequeño',8,'Matorral litoral y garriga ventosa','Endémica de Baleares','Residente','De especial protección','Plan de conservación de la curruca balear','Primavera','Sensible a la perturbación costera','../imgAves/sylvia_balearica.jpg',5),(19,'Monticola solitarius','Roquero solitario','Muscicapidae','Passeriformes',0,'Ave azulada que habita acantilados y zonas rocosas, donde se alimenta de insectos y pequeños invertebrados. Los machos muestran un plumaje intenso durante la temporada de cría para atraer parejas. Requiere roquedos y estructuras elevadas para nidificar, siendo un indicativo de ecosistemas rocosos bien conservados.','ligero','mediano',12,'Zonas rocosas y acantilados','Litoral','Residente','De interés especial',NULL,'Primavera','Machos de color azul intenso','../imgAves/monticola_solitarius.jpg',7),(20,'Falco tinnunculus','Cernícalo vulgar','Falconidae','Falconiformes',0,'Pequeña rapaz muy común en la isla que caza pequeños mamíferos y aves. Se adapta bien a zonas agrícolas y urbanas, utilizando edificios y estructuras para anidar. Su presencia indica ecosistemas equilibrados y la capacidad de las especies rapaces de coexistir con actividades humanas moderadas, aunque requiere vigilancia frente a pesticidas y perturbaciones.','ligero','pequeño',14,'Campos y zonas urbanas','Campos abiertos','Residente','De interés especial',NULL,'Primavera','Usa edificios para anidar','../imgAves/falco_tinnunculus.jpg',NULL),(21,'Anas platyrhynchos','Ánade real','Anatidae','Anseriformes',0,'Pato común de humedales y zonas acuáticas, con dieta variada de plantas y pequeños invertebrados. Nidifica en orillas protegidas y es adaptable a entornos urbanos y rurales. Su población se mantiene gracias a la disponibilidad de agua y protección de humedales, aunque puede hibridar con especies domésticas. Es un indicador del estado ecológico de lagunas y canales.','medio','mediano',10,'Lagunas, canales y salinas','Humedales mallorquines','Residente/Hivernante','De interés especial',NULL,'Primavera','Puede hibridar con domésticos','../imgAves/anas_platyrhynchos.jpg',NULL),(22,'Alcedo atthis','Martín pescador común','Alcedinidae','Coraciiformes',0,'Pequeña ave azul metálica que caza peces desde posaderos sobre aguas tranquilas. Nidifica en taludes blandos excavando galerías para proteger a sus crías, lo que la hace dependiente de márgenes de ríos bien conservados. Su presencia indica calidad del agua y ecosistemas acuáticos saludables.','ligero','pequeño',8,'Aguas dulces lentas con taludes naturales','Ríos y torrentes','Residente local','De interés especial',NULL,'Primavera','Requiere taludes blandos para anidar','../imgAves/alcedo_atthis.jpg',NULL),(23,'Psittacula krameri','Cotorra de Kramer','Psittacidae','Psittaciformes',0,'Especie invasora procedente de África y Asia, que se ha adaptado a parques urbanos y zonas periurbanas. Se alimenta de semillas y frutas, y compite con especies nativas por cavidades para anidar. Su expansión requiere gestión de poblaciones y control para minimizar impactos sobre la biodiversidad local.','medio','mediano',20,'Parques urbanos y periurbanos','N/A','Residente','Invasoras',NULL,'Primavera','Compite por cavidades con especies nativas','../imgAves/psittacula_krameri.jpg',NULL),(24,'Myiopsitta monachus','Cotorra argentina','Psittacidae','Psittaciformes',0,'Especie invasora sudamericana que forma grandes colonias urbanas y periurbanas, construyendo nidos comunales masivos. Se alimenta de semillas y frutos, y puede desplazar a especies nativas de cavidades. Su control es necesario para evitar impactos ecológicos significativos y proteger la biodiversidad local, especialmente en palmerales y parques urbanos.','medio','mediano',20,'Ciudades y palmerales','N/A','Residente','Invasoras',NULL,'Primavera','Construye nidos comunales gigantes','../imgAves/myiopsitta_monachus.jpg',NULL),(25,'Regulus ignicapilla balearicus','Reyezuelo balear','Regulidae','Passeriformes',1,'Subespecie balear del reyezuelo listado que habita bosques de pino y encina. Muy pequeño y activo, se alimenta de insectos diminutos y larvas. Su área de reproducción es limitada, por lo que requiere protección de hábitats forestales y seguimiento poblacional. Es una especie emblemática de la fauna insular, con comportamientos sociales discretos y nidificación poco extensa.','ligero','muy pequeño',6,'Bosques de pino y encina','Serra de Tramuntana','Residente','De especial protección',NULL,'Primavera','No se reproduce ampliamente en la isla','../imgAves/regulus_ignicapilla_balearicus.jpg',7),(26,'Parus caeruleus balearicus','Herrerillo común balear','Paridae','Passeriformes',1,'Subespecie balear del herrerillo adaptada a pinares y bosques mixtos. Se alimenta de insectos y semillas, y utiliza cavidades naturales o nidos artificiales para reproducirse. Su plumaje azul distintivo y su comportamiento activo lo hacen fácilmente reconocible. Es una especie protegida y sirve como indicador de la calidad de los ecosistemas forestales de Mallorca y Menorca.','ligero','pequeño',8,'Bosques de pino y encina','Tramuntana y Llevant','Residente','De especial protección',NULL,'Primavera','Variante insular azulada','../imgAves/parus_caeruleus_balearicus.jpg',7),(27,'Muscicapa striata balearica','Papamoscas balear','Muscicapidae','Passeriformes',1,'Subespecie balear del papamoscas gris, pequeña y discreta, que habita bosques abiertos y olivares. Se alimenta de insectos y nidifica en cavidades naturales. Su población requiere conservación de hábitats abiertos y árboles maduros, ya que su reproducción depende de estructuras de nidificación seguras y de una buena disponibilidad de presas durante la primavera.','ligero','pequeño',9,'Bosques abiertos y olivares','Sur de Europa y África','Migradora parcial','De especial protección',NULL,'Primavera','Cría en cavidades naturales','../imgAves/muscicapa_striata_balearica.jpg',NULL),(28,'Aquila fasciata','Águila perdicera','Accipitridae','Accipitriformes',0,'Rapaz en peligro en Baleares que habita zonas rocosas y montañosas. Se alimenta de aves y pequeños mamíferos, y requiere grandes territorios para cazar y reproducirse. Su baja densidad poblacional y sensibilidad a disturbios humanos hacen que los planes de recuperación y conservación sean esenciales para evitar su extinción local, protegiendo nidos y áreas de alimentación.','pesado','grande',30,'Zonas rocosas y montañosas','Mediterráneo','Residente','Peligro de extinción','Plan de recuperación Águila perdicera','Primavera','Muy escasa en Mallorca','../imgAves/aquila_fasciata.jpg',7),(29,'Aythya nyroca','Porrón pardo','Anatidae','Anseriformes',0,'Pato buceador escaso que habita lagunas interiores y humedales del sur de Mallorca. Se alimenta de plantas acuáticas y pequeños invertebrados. Su población se encuentra vulnerable debido a la pérdida de hábitat y la presión humana. La protección de aguas tranquilas y zonas de reproducción es fundamental para asegurar su supervivencia en las islas.','medio','mediano',10,'Lagunas interiores','Humedales del sur','Migrador/Residente','Vulnerables',NULL,'Primavera','Prefiere aguas tranquilas','../imgAves/aythya_nyroca.jpg',NULL),(30,'Botaurus stellaris','Avetoro común','Ardeidae','Pelecaniformes',0,'Gran garza críptica que habita cañizales densos y tranquilos. Muy rara en Mallorca, se alimenta de peces, anfibios e invertebrados. Su camuflaje le permite pasar desapercibida, pero también dificulta su observación. La conservación de cañizales intactos y la minimización de perturbaciones humanas son esenciales para asegurar su supervivencia y reproducción exitosa.','pesado','grande',25,'Cañizales densos y tranquilos','Humedales','Residente parcial','Peligro de extinción',NULL,'Primavera','Muy difícil de observar por su camuflaje','../imgAves/botaurus_stellaris.jpg',NULL),(31,'Fulica cristata','Focha cornuda','Rallidae','Gruiformes',0,'Ave acuática de humedales con aguas abiertas, reconocible por su pico y cresta distintivos. Se alimenta de vegetación acuática y pequeños invertebrados. Su población es sensible a sequías, caza furtiva y cambios en la gestión de humedales. La protección de lagunas y canales, así como la conservación de áreas húmedas, es clave para mantener esta especie amenazada en Baleares.','medio','mediano',15,'Lagunas y canales','Aiguamolls','Residente','Peligro de extinción',NULL,'Primavera','Sensible a sequías y caza furtiva','../imgAves/fulica_cristata.jpg',NULL),(32,'Marmaronetta angustirostris','Cerceta pardilla','Anatidae','Anseriformes',0,'Pato en situación crítica en Baleares, que habita lagunas salobres y balsas temporales. Se alimenta de vegetación acuática y pequeños invertebrados. Su población ha disminuido drásticamente por la pérdida de hábitat y la degradación de humedales. La conservación requiere protección activa de lagunas, control de depredadores y gestión adecuada de recursos hídricos.','ligero','mediano',10,'Lagunas salobres y balsas','África','Migradora','Peligro de extinción',NULL,'Primavera','En declive por pérdida de hábitat','../imgAves/marmaronetta_angustirostris.jpg',NULL),(33,'Oxyura leucocephala','Malvasía cabeciblanca','Anatidae','Anseriformes',0,'Pato buceador en peligro de extinción que habita lagunas permanentes de Mallorca. Se alimenta de vegetación acuática y pequeños invertebrados. Su población se beneficia de programas de recuperación y conservación, incluyendo la protección de nidos y control de depredadores. Es fácilmente reconocible por su pico azul y su cola rígida, siendo un símbolo de la fauna de humedales mediterráneos.','medio','mediano',12,'Lagunas permanentes','Humedales de Mallorca','Residente','Peligro de extinción','Plan de recuperación Malvasía','Primavera','Reconocible por su pico azul','../imgAves/oxyura_leucocephala.jpg',NULL),(34,'Gyps fulvus','Buitre leonado','Accipitridae','Accipitriformes',0,'Rapaz carroñera que habita zonas montañosas y amplias áreas abiertas, alimentándose de carroña. Es clave para la limpieza del ecosistema y la prevención de enfermedades. Puede volar grandes distancias en busca de alimento y requiere protección de sus nidos y áreas de descanso. Su conservación depende de la gestión sostenible de los espacios montañosos y la prevención de intoxicaciones accidentales.','pesado','grande',30,'Zonas montañosas','Serra de Tramuntana','Residente','De especial protección',NULL,'Primavera','Puede volar grandes distancias','../imgAves/gyps_fulvus.jpg',7),(35,'Circus aeruginosus','Aguilucho lagunero','Accipitridae','Accipitriformes',0,'Rapaz ligada a humedales que se alimenta de pequeños mamíferos, aves e insectos. Nidifica en carrizales y marismas, siendo muy sensible a la perturbación humana. La protección de humedales y vegetación palustre es esencial para la conservación de esta especie, que actúa como indicador de ecosistemas acuáticos saludables.','medio','mediano',20,'Carrizales y marismas','Humedales costeros','Residente','De interés especial',NULL,'Primavera','Cría entre la vegetación palustre','../imgAves/circus_aeruginosus.jpg',1),(36,'Egretta garzetta','Garceta común','Ardeidae','Pelecaniformes',0,'Pequeña garza blanca frecuente en s’Albufera y otros humedales costeros. Se alimenta de peces y pequeños invertebrados acuáticos, formando colonias mixtas durante la reproducción. Su presencia indica la salud de los humedales y requiere áreas de alimentación protegidas y vegetación acuática para nidificación segura.','ligero','mediano',18,'Lagunas y canales','Humedales','Residente parcial','De interés especial',NULL,'Primavera','Muy sociable en colonias mixtas','../imgAves/egretta_garzetta.jpg',1),(37,'Nycticorax nycticorax','Martinete común','Ardeidae','Pelecaniformes',0,'Garza nocturna que se alimenta al atardecer y al anochecer, cazando peces, anfibios e invertebrados. Habita carrizales y canales, siendo difícil de observar durante el día. Su conservación depende de la protección de humedales tranquilos y vegetación densa para nidificación y alimentación segura.','ligero','mediano',18,'Carrizales y canales','Humedales','Residente parcial','De interés especial',NULL,'Primavera','Activa al anochecer','../imgAves/nycticorax_nycticorax.jpg',1),(38,'Bubulcus ibis','Garcilla bueyera','Ardeidae','Pelecaniformes',0,'Garza pequeña asociada a zonas agrícolas, que sigue al ganado para alimentarse de insectos. Nidifica en colonias y se adapta a paisajes modificados por el hombre. Su presencia indica ecosistemas agrícolas con agua y pastos adecuados, siendo un ejemplo de adaptación de aves a entornos humanos.','ligero','mediano',15,'Campos húmedos y ganaderos','Campos de cultivo','Residente','De interés especial',NULL,'Primavera','Sigue al ganado para alimentarse','../imgAves/bubulcus_ibis.jpg',NULL),(39,'Tringa totanus','Archibebe común','Scolopacidae','Charadriiformes',0,'Limícola común en pasos migratorios, que habita arenales y humedales costeros. Se alimenta de invertebrados en la orilla y requiere áreas de descanso durante la migración. En Baleares, su cría es escasa y depende de playas tranquilas y salinas protegidas.','ligero','mediano',12,'Arenales y humedales costeros','África','Migrador','De interés especial',NULL,'Primavera','Cría escasa en Baleares','../imgAves/tringa_totanus.jpg',NULL),(40,'Rallus aquaticus','Rascón europeo','Rallidae','Gruiformes',0,'Ave esquiva que habita carrizales densos, alimentándose de invertebrados y vegetación. Es muy difícil de observar por su hábito oculto y requiere humedales densos y protegidos para reproducirse. Su conservación depende de mantener vegetación palustre intacta y minimizar perturbaciones humanas.','ligero','pequeño',10,'Carrizales y marismas','Humedales','Residente','De interés especial',NULL,'Primavera','Difícil de observar por su hábito oculto','../imgAves/rallus_aquaticus.jpg',1),(41,'Accipiter nisus','Gavilán común','Accipitridae','Accipitriformes',0,'Pequeña rapaz migratoria que se alimenta de pequeños pájaros y mamíferos. Habita bosques y zonas rurales, siendo ágil cazador entre la vegetación. Su conservación requiere la protección de hábitats forestales y la reducción de perturbaciones durante la reproducción.','ligero','mediano',15,'Bosques y zonas rurales','Europa central','Migrador','De interés especial',NULL,'Primavera','Depreda pequeños pájaros','../imgAves/accipiter_nisus.jpg',NULL),(42,'Aegithalos caudatus','Mito','Aegithalidae','Passeriformes',0,'Pequeño paseriforme con larga cola que habita bosques y parques. Se alimenta de insectos y pequeños frutos, formando grupos familiares ruidosos y sociales. Su comportamiento activo y la cooperación entre individuos lo hacen fascinante, siendo un indicador de ecosistemas boscosos saludables y biodiversos.','ligero','pequeño',9,'Bosques y parques','Europa','Residente','De interés especial',NULL,'Primavera','Forma grupos familiares ruidosos','../imgAves/aegithalos_caudatus.jpg',NULL),(43,'Aquila chrysaetos','Águila real','Accipitridae','Accipitriformes',0,'Gran rapaz montañosa presente en Mallorca, que caza mamíferos medianos y aves. Ocupa grandes territorios y es símbolo de poder en la fauna ibérica. Su conservación requiere protección de áreas montañosas y nidos, evitando perturbaciones humanas y degradación del hábitat.','pesado','grande',40,'Zonas rocosas y montañosas','Serra de Tramuntana','Residente','De especial protección',NULL,'Primavera','Símbolo de poder en la fauna ibérica','../imgAves/aquila_chrysaetos.jpg',7),(44,'Erithacus rubecula','Petirrojo europeo','Muscicapidae','Passeriformes',0,'Pequeña ave insectívora, residente parcial en Mallorca, que se alimenta de insectos y frutos. Muy familiar en invierno y activa en jardines y bosques, contribuyendo al control de plagas. Su observación es común en entornos cercanos a la actividad humana, siendo un ejemplo de convivencia de aves silvestres con paisajes humanizados.','ligero','pequeño',8,'Bosques y jardines','Europa','Migrador parcial','De interés especial',NULL,'Primavera','Muy familiar en invierno','../imgAves/erithacus_rubecula.jpg',NULL),(45,'Geranoaetus melanoleucus','Aguilucho negro-gris','Accipitridae','Accipitriformes',0,'Especie invasora sudamericana que habita áreas abiertas y urbanas, depredando pequeños mamíferos y aves. Su expansión representa un desafío para la fauna local, pudiendo competir con rapaces nativas. La gestión de su población es importante para minimizar impactos ecológicos y proteger la biodiversidad.','pesado','grande',25,'Áreas abiertas y urbanas','Sudamérica','Residente','Invasoras',NULL,'Primavera','Depredador oportunista','../imgAves/geranoaetus_melanoleucus.jpg',NULL),(46,'Acridotheres tristis','Miná común','Sturnidae','Passeriformes',0,'Ave invasora asiática en expansión, que se adapta a zonas urbanas y agrícolas. Se alimenta de semillas, frutas e insectos, compitiendo con especies locales por recursos y cavidades. Su presencia creciente requiere vigilancia y control para reducir impactos sobre aves autóctonas y ecosistemas urbanos y rurales.','ligero','mediano',12,'Zonas urbanas y agrícolas','Asia','Residente','Invasoras',NULL,'Primavera','Compite con especies locales','../imgAves/acridotheres_tristis.jpg',NULL),(47,'Estrilda astrild','Estrilda común','Estrildidae','Passeriformes',0,'Ave invasora africana muy gregaria, que habita campos húmedos y cañizales. Se alimenta de semillas y forma grandes grupos sociales. Su comportamiento sociable y colorido contrasta con especies nativas, y requiere gestión para evitar impactos ecológicos en humedales y pastizales.','ligero','pequeño',10,'Campos húmedos y cañizales','África','Residente','Invasoras',NULL,'Primavera','Pequeña y colorida, muy social','../imgAves/estrilda_astrild.jpg',NULL);

INSERT INTO `Equipo` VALUES (1,'María González López','Coordinadora de Proyecto','Especialista en ornitología mediterránea con más de 10 años de experiencia en conservación de especies endémicas de las Baleares.','Aves marinas y rapaces','../imgPeople/woman.jpg',1),(2,'Jordi Martínez Soler','Investigador de Campo','Biólogo especializado en seguimiento de poblaciones de aves y análisis de datos sobre biodiversidad insular.','Especies endémicas y migratorias','../imgPeople/guy1.jpg',2),(3,'Laura Vidal Ramírez','Responsable de Educación Ambiental','Educadora ambiental dedicada a la divulgación científica y organización de actividades de sensibilización sobre conservación.','Comunicación y educación','../imgPeople/woman2.jpg',3),(4,'Carlos Fernández Torres','Desarrollador Web','Desarrollador encargado del diseño y mantenimiento de la plataforma digital, facilitando el acceso a información ornitológica.','Desarrollo web y bases de datos','../imgPeople/guy2.jpg',4);

